import React, { useState, useRef, useEffect } from "react";
import "./Homepage.css";
import logo from "../assets/logo.jpg";
import bloo1 from "../assets/bloo1.jpg";
import bloo2 from "../assets/bloo2.jpg";
import bloo3 from "../assets/bloo3.jpg";
import bloo4 from "../assets/bloo4.jpg";
import bloo5 from "../assets/bloo5.jpg";
import bloo6 from "../assets/bloo6.jpg";

const Homepage = () => {
  const [formData, setFormData] = useState({
    serviceType: "",
    fullName: "",
    email: "",
    dateNaissance: "",
    levelEducation: "",
    languageLevel: "",
    message: "",
    files: [],
    acceptTerms: false,
  });

  const [scroll, setScroll] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);
  const [activeSection, setActiveSection] = useState("hero");
  const [isSubmitting, setIsSubmitting] = useState(false);

  const sectionRefs = {
    hero: useRef(null),
    about: useRef(null),
    services: useRef(null),
    portfolio: useRef(null),
    contact: useRef(null),
  };

  const toggleMenu = () => setMenuOpen(!menuOpen);

  // Navbar scroll effect
  useEffect(() => {
    const handleScroll = () => {
      setScroll(window.scrollY > 50);
      const sections = Object.keys(sectionRefs);
      const current = sections.find((section) => {
        const element = sectionRefs[section].current;
        if (!element) return false;
        const rect = element.getBoundingClientRect();
        return rect.top <= 100 && rect.bottom >= 100;
      });
      if (current) setActiveSection(current);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Smooth scroll
  useEffect(() => {
    const links = document.querySelectorAll('a[href^="#"]');
    links.forEach((link) => {
      link.addEventListener("click", function (e) {
        e.preventDefault();
        const targetId = this.getAttribute("href").substring(1);
        const target = document.getElementById(targetId);
        if (target) {
          target.scrollIntoView({ behavior: "smooth" });
          setActiveSection(targetId);
        }
      });
    });
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (files) {
      setFormData({ ...formData, files: [...formData.files, ...Array.from(files)] });
    } else if (type === "checkbox") {
      setFormData({ ...formData, [name]: checked });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const data = new FormData();
      for (const key of ["serviceType","fullName","email","dateNaissance","levelEducation","languageLevel","message"]) {
        if (formData[key]) data.append(key, formData[key]);
      }
      formData.files.forEach((file) => data.append("files", file));
      data.append("acceptTerms", formData.acceptTerms);

      const res = await fetch("http://localhost:5000/send-email", {
        method: "POST",
        body: data,
      });
      const result = await res.json();
      alert(result.message);

      setFormData({
        serviceType: "",
        fullName: "",
        email: "",
        dateNaissance: "",
        levelEducation: "",
        languageLevel: "",
        message: "",
        files: [],
        acceptTerms: false,
      });
    } catch (err) {
      console.error(err);
      alert("Failed to submit form. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="homepage">
      {/* Navbar */}
      <nav className={`navbar ${scroll ? "navbar-scrolled" : ""}`}>
        <div className="navbar-container">
          <div className="logo-container">
            <img src={logo} alt="Bloo Digitally" className="logo-img" />
            <span className="logo-text">Bloo Digitally</span>
          </div>
          <ul className={`nav-links ${menuOpen ? "nav-active" : ""}`}>
            {["hero","about","services","portfolio","contact"].map((section) => (
              <li key={section}>
                <a href={`#${section}`} className={activeSection===section ? "active" : ""}>
                  {section.charAt(0).toUpperCase() + section.slice(1)}
                </a>
              </li>
            ))}
          </ul>
          <div className={`burger ${menuOpen ? "toggle" : ""}`} onClick={toggleMenu}>
            <div className="line1"></div>
            <div className="line2"></div>
            <div className="line3"></div>
          </div>
        </div>
      </nav>

      {/* Hero */}
      <section id="hero" ref={sectionRefs.hero} className="hero-section">
        <h1>Welcome to Bloo Digitally</h1>
        <p>Your digital growth partner</p>
      </section>

      {/* About */}
      <section id="about" ref={sectionRefs.about} className="about-section">
        <h2>About Us</h2>
        <p>Bloo Digitally is dedicated to connecting talents with opportunities worldwide.</p>
      </section>

      {/* Services / Form */}
      <section id="services" ref={sectionRefs.services} className="services-section">
        <h2>Get in Touch</h2>
        <form className="candidate-form" onSubmit={handleSubmit}>
          <select name="serviceType" value={formData.serviceType} onChange={handleChange} required>
            <option value="">Select Service</option>
            <option value="Job Search">Job Search</option>
            <option value="Study Abroad">Study Abroad</option>
            <option value="Volunteering">Volunteering</option>
          </select>

          <input type="text" name="fullName" placeholder="Full Name" value={formData.fullName} onChange={handleChange} required/>
          <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required/>
          <input type="date" name="dateNaissance" value={formData.dateNaissance} onChange={handleChange}/>
          <input type="text" name="levelEducation" placeholder="Level of Education" value={formData.levelEducation} onChange={handleChange}/>
          <input type="text" name="languageLevel" placeholder="Language Level (A1-C2)" value={formData.languageLevel} onChange={handleChange}/>
          <textarea name="message" placeholder="Message / Cover Letter" value={formData.message} onChange={handleChange}/>

          <input type="file" multiple onChange={handleChange} />

          <label>
            <input type="checkbox" name="acceptTerms" checked={formData.acceptTerms} onChange={handleChange}/> I accept the terms
          </label>

          <button type="submit" disabled={isSubmitting}>{isSubmitting ? "Submitting..." : "Submit"}</button>
        </form>
      </section>

      {/* Portfolio */}
      <section id="portfolio" ref={sectionRefs.portfolio} className="portfolio-section">
        <h2>Our Work</h2>
        <div className="portfolio-grid">
          {[bloo1,bloo2,bloo3,bloo4,bloo5,bloo6].map((img, i) => <img key={i} src={img} alt={`Portfolio ${i+1}`} />)}
        </div>
      </section>

      {/* Contact */}
      <section id="contact" ref={sectionRefs.contact} className="contact-section">
        <h2>Contact Us</h2>
        <p>Email: contact@bloo.com</p>
      </section>
    </div>
  );
};

export default Homepage;
