import React, { useEffect, useState } from "react";

import "./Homepage.css";
import bloo1 from "../assets/bloo1.jpg";
import bloo2 from "../assets/bloo2.jpg";
import bloo3 from "../assets/bloo3.jpg";
import bloo4 from "../assets/bloo4.jpg";
import bloo5 from "../assets/bloo5.jpg";
import bloo6 from "../assets/bloo6.jpg";

const Homepage = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    phone: "",
    linkedin: "",
    message: "",
    resume: null,
  });


  // Smooth scroll for anchor links
  useEffect(() => {
    const links = document.querySelectorAll('a[href^="#"]');
    links.forEach((link) => {
      link.addEventListener("click", function (e) {
        e.preventDefault();
        const target = document.querySelector(this.getAttribute("href"));
        target.scrollIntoView({ behavior: "smooth" });
      });
    });
  }, []);

  // Handle input change
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "resume") {
      setFormData({ ...formData, resume: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  // Handle form submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append("fullName", formData.fullName);
    data.append("email", formData.email);
    data.append("phone", formData.phone);
    data.append("linkedin", formData.linkedin);
    data.append("message", formData.message);
    if (formData.resume) {
      data.append("resume", formData.resume);
    }

    try {
      const res = await fetch("http://localhost:5000/send-email", {
        method: "POST",
        body: data,
      });
      const result = await res.json();
      alert(result.message);
      // Reset form
      setFormData({
        fullName: "",
        email: "",
        phone: "",
        linkedin: "",
        message: "",
        resume: null,
      });
    } catch (err) {
      console.error(err);
      alert("Failed to submit form.");
    }
  };

  return (
    <div className="homepage">

      {/* Navbar */}
<nav className={`navbar ${scroll ? "navbar-scrolled" : ""}`}>
  <div className="navbar-container">
    <h1 className="logo">Bloo Digitally</h1>
    <ul className="nav-links">
      <li><a href="#hero">Home</a></li>
      <li><a href="#about">About</a></li>
      <li><a href="#services">Services</a></li>
      <li><a href="#portfolio">Portfolio</a></li>
      <li><a href="#contact">Contact</a></li>
    </ul>
    <div className="mobile-menu-icon" onClick={toggleMenu}>
      <span></span>
      <span></span>
      <span></span>
    </div>
  </div>
</nav>

      {/* Hero Section */}
      <section className="hero animate-fadeIn">
        <div className="hero-content">
          <h1>Bloo Digitally</h1>
          <p>Your trusted partner in digital consulting</p>
          <a  className="btn-primary">
            Get Started
          </a>
        </div>
        <img src={bloo1} alt="Hero" className="hero-img" />
      </section>

      {/* About Section */}
      <section className="about animate-slideIn">
        <div className="about-content">
          <h2>About Us</h2>
          <p>
            At Bloo Digitally, we specialize in helping businesses transform
            digitally with tailored strategies and solutions.
          </p>
        </div>
        <img src={bloo6} alt="About" className="about-img" />
      </section>

   {/* Services Section */}
<section className="services animate-fadeIn">
  <h2>Our Services</h2>
  <div className="service-carousel">
    <div className="service-track">
      <div className="card">
        <img src={bloo3} alt="Service 1" />
        <h3>Digital Strategy</h3>
        <p>Comprehensive strategies to boost your online presence.</p>
      </div>
      <div className="card">
        <img src={bloo4} alt="Service 2" />
        <h3>Web Development</h3>
        <p>Modern, responsive, and scalable websites.</p>
      </div>

      {/* Duplicate cards for infinite loop */}
      <div className="card">
        <img src={bloo3} alt="Service 1" />
        <h3>Digital Strategy</h3>
        <p>Comprehensive strategies to boost your online presence.</p>
      </div>
      <div className="card">
        <img src={bloo4} alt="Service 2" />
        <h3>Web Development</h3>
        <p>Modern, responsive, and scalable websites.</p>
      </div>
      <div className="card">
        <img src={bloo4} alt="Service 3" />
        <h3>Marketing Solutions</h3>
        <p>Targeted campaigns to drive measurable results.</p>
      </div>
    </div>
  </div>
</section>


      {/* Portfolio Section */}
      <section className="portfolio animate-slideIn">
        <h2>Our Portfolio</h2>
        <div className="portfolio-grid">
          <img src={bloo3} alt="Portfolio 1" />
          <img src={bloo4} alt="Portfolio 2" />

        </div>
      </section>


      {/* Testimonials Section */}
<section className="testimonials animate-slideIn">
  <h2>What Our Clients Say</h2>
  <div className="testimonial-carousel">
    <div className="testimonial-track">
      <div className="testimonial-card">
        <p>"Bloo Digitally transformed our online presence. Their team is amazing!"</p>
        <h4>- John Doe, CEO of TechCorp</h4>
      </div>
      <div className="testimonial-card">
        <p>"Professional and reliable digital solutions. Highly recommended!"</p>
        <h4>- Sarah Lee, Marketing Director</h4>
      </div>
      <div className="testimonial-card">
        <p>"Our website traffic increased by 300% thanks to their strategy."</p>
        <h4>- Ahmed Khan, Founder of StartUpX</h4>
      </div>

      {/* Duplicate cards for infinite scroll */}
      <div className="testimonial-card">
        <p>"Bloo Digitally transformed our online presence. Their team is amazing!"</p>
        <h4>- John Doe, CEO of TechCorp</h4>
      </div>
      <div className="testimonial-card">
        <p>"Professional and reliable digital solutions. Highly recommended!"</p>
        <h4>- Sarah Lee, Marketing Director</h4>
      </div>
    </div>
  </div>
</section>


{/* Statistics & Achievements Section */}
<section className="statistics animate-fadeIn">
  <h2>Our Achievements</h2>
  <div className="stats-grid">
    <div className="stat-card">
      <h3>500+</h3>
      <p>Projects Completed</p>
    </div>
    <div className="stat-card">
      <h3>120+</h3>
      <p>Happy Clients</p>
    </div>
    <div className="stat-card">
      <h3>15</h3>
      <p>Years of Experience</p>
    </div>
    <div className="stat-card">
      <h3>10</h3>
      <p>Awards Won</p>
    </div>
  </div>
</section>


    {/* Contact Section */}
      <section className="contact animate-fadeIn">
        <h2>Contact Us</h2>
        <form className="contact-form" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Your Name"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            required
          />
          <input
            type="email"
            placeholder="Your Email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            placeholder="Phone"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
          />
          <input
            type="text"
            placeholder="LinkedIn URL"
            name="linkedin"
            value={formData.linkedin}
            onChange={handleChange}
          />
          <textarea
            placeholder="Your Message"
            name="message"
            rows="4"
            value={formData.message}
            onChange={handleChange}
            required
          ></textarea>
          <input
            type="file"
            name="resume"
            accept=".pdf,.doc,.docx"
            onChange={handleChange}
          />
          <button type="submit" className="btn-primary">
            Send Message
          </button>
        </form>
      </section>
      {/* Footer */}
      <footer className="footer animate-fadeIn">
        <p> Bloo Digitally. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Homepage;
