import React, { useEffect, useState } from "react";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState("");
  const [selectedUser, setSelectedUser] = useState(null);
  const [page, setPage] = useState(1);
  const [limit] = useState(10);
  const [total, setTotal] = useState(0);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  // Fetch users from backend
  const fetchUsers = async () => {
    try {
      const res = await fetch(
        `http://localhost:5000/candidates?page=${page}&limit=${limit}`
      );
      const data = await res.json();
      setUsers(data.candidates || data); 
      setTotal(data.total || data.length);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, [page]);

  const filteredUsers = users.filter(
    (user) =>
      user.fullName.toLowerCase().includes(search.toLowerCase()) ||
      user.email.toLowerCase().includes(search.toLowerCase())
  );

  const downloadFile = (file) => {
    const blob = new Blob([new Uint8Array(file.data.data)], {
      type: file.contentType,
    });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = file.filename;
    link.click();
  };

  return (
    <div className="dashboard-container">
      <aside className={`sidebar ${sidebarOpen ? "sidebar-open" : ""}`}>
        <div className="sidebar-header">
          <h2>Admin Panel</h2>
          <button className="toggle-sidebar" onClick={toggleSidebar}>
            {sidebarOpen ? "⮜" : "⮞"}
          </button>
        </div>
        <ul className="sidebar-links">
          <li>Candidates</li>
        </ul>
      </aside>

      <main className="main-content">
        <h1>Admin Dashboard</h1>

        <input
          type="text"
          placeholder="Search by name or email..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="search-input"
        />

        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Phone</th>
                <th>LinkedIn</th>
                <th>Poste</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredUsers.map((user) => (
                <tr key={user._id}>
                  <td>{user.fullName}</td>
                  <td>{user.email}</td>
                  <td>{user.phone || "N/A"}</td>
                  <td>{user.linkedin || "N/A"}</td>
                  <td>{user.poste || "N/A"}</td>
                  <td>
                    <button
                      onClick={() => setSelectedUser(user)}
                      className="btn-view"
                    >
                      View
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        <div className="pagination">
          {Array.from({ length: Math.ceil(total / limit) }, (_, i) => (
            <button
              key={i}
              onClick={() => setPage(i + 1)}
              className={page === i + 1 ? "page-active" : ""}
            >
              {i + 1}
            </button>
          ))}
        </div>

        {selectedUser && (
          <div className="modal-backdrop">
            <div className="modal">
              <h2>{selectedUser.fullName}</h2>
              <p><strong>Email:</strong> {selectedUser.email}</p>
              <p><strong>Phone:</strong> {selectedUser.phone || "N/A"}</p>
              <p><strong>LinkedIn:</strong> {selectedUser.linkedin || "N/A"}</p>
              <p><strong>Poste:</strong> {selectedUser.poste || "N/A"}</p>
              <p><strong>Date of Birth:</strong> {selectedUser.dateNaissance ? new Date(selectedUser.dateNaissance).toLocaleDateString() : "N/A"}</p>
              <p><strong>Languages:</strong> French - {selectedUser.languages?.french || "N/A"}, English - {selectedUser.languages?.english || "N/A"}</p>
              <p><strong>Interested Countries:</strong> {selectedUser.interestedCountries?.join(", ") || "N/A"}</p>
              <p><strong>Accepted Terms:</strong> {selectedUser.acceptTerms ? "Yes" : "No"}</p>
              <p><strong>Message:</strong> {selectedUser.message || "N/A"}</p>

              {selectedUser.resume && (
                <button
                  onClick={() => downloadFile(selectedUser.resume)}
                  className="btn-download"
                >
                  Download Resume
                </button>
              )}
              {selectedUser.cin && (
                <button
                  onClick={() => downloadFile(selectedUser.cin)}
                  className="btn-download"
                >
                  Download CIN
                </button>
              )}
              {selectedUser.passport && (
                <button
                  onClick={() => downloadFile(selectedUser.passport)}
                  className="btn-download"
                >
                  Download Passport
                </button>
              )}

              <button
                onClick={() => setSelectedUser(null)}
                className="btn-close"
              >
                Close
              </button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default AdminDashboard;
