import React, { useEffect, useState } from "react";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState("");
  const [selectedUser, setSelectedUser] = useState(null);
  const [page, setPage] = useState(1);
  const [limit] = useState(10);
  const [total, setTotal] = useState(0);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  // Fetch users from backend
  const fetchUsers = async () => {
    try {
      const res = await fetch(
        `http://localhost:5000/candidates?page=${page}&limit=${limit}`
      );
      const data = await res.json();
      setUsers(data.candidates || data); // Adjust if backend returns array directly
      setTotal(data.total || data.length);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, [page]);

  // Filtered users based on search
  const filteredUsers = users.filter(
    (user) =>
      user.fullName.toLowerCase().includes(search.toLowerCase()) ||
      user.email.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <aside className={`sidebar ${sidebarOpen ? "sidebar-open" : ""}`}>
        <div className="sidebar-header">
          <h2>Admin Panel</h2>
          <button className="toggle-sidebar" onClick={toggleSidebar}>
            {sidebarOpen ? "⮜" : "⮞"}
          </button>
        </div>
        <ul className="sidebar-links">
          <li>Candidates</li>
        </ul>
      </aside>

      {/* Main content */}
      <main className="main-content">
        <h1>Admin Dashboard</h1>

        {/* Search */}
        <input
          type="text"
          placeholder="Search by name or email..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="search-input"
        />

        {/* Users Table */}
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Phone</th>
                <th>LinkedIn</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredUsers.map((user) => (
                <tr key={user._id}>
                  <td>{user.fullName}</td>
                  <td>{user.email}</td>
                  <td>{user.phone || "N/A"}</td>
                  <td>{user.linkedin || "N/A"}</td>
                  <td>
                    <button
                      onClick={() => setSelectedUser(user)}
                      className="btn-view"
                    >
                      View
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Pagination */}
        <div className="pagination">
          {Array.from({ length: Math.ceil(total / limit) }, (_, i) => (
            <button
              key={i}
              onClick={() => setPage(i + 1)}
              className={page === i + 1 ? "page-active" : ""}
            >
              {i + 1}
            </button>
          ))}
        </div>

        {/* User Modal */}
        {selectedUser && (
          <div className="modal-backdrop">
            <div className="modal">
              <h2>{selectedUser.fullName}</h2>
              <p>
                <strong>Email:</strong> {selectedUser.email}
              </p>
              <p>
                <strong>Phone:</strong> {selectedUser.phone || "N/A"}
              </p>
              <p>
                <strong>LinkedIn:</strong> {selectedUser.linkedin || "N/A"}
              </p>
              <p>
                <strong>Message:</strong> {selectedUser.message || "N/A"}
              </p>
            {selectedUser.resume && (
            <a
    href={`data:${selectedUser.resume.contentType};base64,${btoa(
      String.fromCharCode(...new Uint8Array(selectedUser.resume.data.data))
    )}`}
    download={selectedUser.resume.filename}
    className="btn-download inline-block mr-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
  >
    Download Resume
           </a>
              )}


              <button
                onClick={() => setSelectedUser(null)}
                className="btn-close"
              >
                Close
              </button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default AdminDashboard;
